import axios, { AxiosError } from 'axios';
import { store } from './store.js'
import { SET_CHATID,SET_CHATNAME,SET_PARTICIPANTS,SET_MANAGERS,SET_MESSAGES,SET_DESCRIPTION } from './userReducer.js'

export async function creatNewChat(chat){
    try{
        const newChat = await axios.post(`http://localhost:3030/api/chat`,chat)
        return newChat.data
    }catch(err){
    }
}

export async function setChat(chatId){
    try{
        const chat = await axios.get(`http://localhost:3030/api/chat/${chatId}`)
        console.log(chat)

        store.dispatch({
            type: SET_CHATID,
            chatId: logInUser.data.userLogIn._id
        })
        store.dispatch({
            type: SET_CHATNAME,
            chatName: logInUser.data.userLogIn.name
        })
        store.dispatch({
            type: SET_PARTICIPANTS,
            participants: logInUser.data.userLogIn.email
        })
        store.dispatch({
            type: SET_MANAGERS,
            managres: logInUser.data.userLogIn.chats
        })
        store.dispatch({
            type: SET_MESSAGES,
            massages: logInUser.data.userLogIn.chats
        })
        store.dispatch({
            type: SET_DESCRIPTION,
            description: logInUser.data.userLogIn.chats
        })

    }catch(err){

    }
}
