import { useSelector } from 'react-redux'
import {ChatMenuHeader} from './chatMenuHeader'
import { useState } from 'react'
import{findUser} from '../store/userActions'

export function ChatsMenu(){
    const user = useSelector(state => state.userModule )

    const [mod,setMod] = useState('chats')
    const [participants,setParticipants] = useState([])    

    const [email,setEmail] = useState('')
    const [userFound,setUserFound] = useState(null)

    async function searchUsser() {
        try{
            const user = await findUser(email)
            const setUser = {
                id:user._id,
                name:user.name
            }
            setUserFound({setUser})
        }catch(err){
            console.log(err)
        }
    }

    console.log('userFound',userFound)

    return <div className="chatsMenu">
        <div className="chatMenuHeader">
            <div className= "userMenu">
                userMenu
            </div>
            <div className='chatMenuActions'>
                <div className="chatMenuBtn" onClick={() => setMod('newGroup')}>New Group</div>
                <div className="chatMenuBtn" onClick={() =>setMod('newChat')}>New Chat</div>
                <div className="chatMenuBtn" onClick={() =>setMod('chats')}>Chats</div>
            </div>
        </div>

       {
        ((mod == 'chats' && !user.userChats) && 'no chats')
       }
       {
        ((mod == 'chats' && !user.userChats) && 'chats')
       }
       {
        (mod == 'newGroup' && <div className='newGroup'>
            <form>
                <input type='text' placeholder='Group name'></input>
                <input type='text' placeholder='Group description'></input>
                <div>look for user</div>
                <input type='text' placeholder='Email' onChange={(e) => setEmail(e.target.value)}></input>
                <input type='button' value={'search'} onClick={() => searchUsser()}></input>
            </form>
        </div>)
       }
        
    </div>
}
