import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import {findUser} from '../store/userActions'

export function ChatHeader({chat}){

    const user = useSelector(state => state.userModule)
    const [user2,setUser2] = useState('')
    const [founder, setFounder] = useState('')
    const [managers,setManagers] = useState([])


    useEffect(() => {
        if(chat.type == 'privet'){
            const user2Id = chat.participants.filter((participant) => participant != user.userId)
            findUser2(user2Id)
        }else{
            setGroup(chat.founder)
        }
    },[])

    async function findUser2(id) {
        try{
            const user = await findUser(id)
            setUser2(user.name)
        }catch(err){}
    }

    async function setGroup(founderId){
        try{
            const founder = await findUser(founderId)
            const founderName = founder.name
            setFounder(founderName)

            const managersNames = []

            for(let manager in chat.managers){
                // console.log(chat.managers)
                const user = await findUser(chat.managers[manager])
                managersNames.push(user.name)
            }

            setManagers(managersNames)

        }catch(err){}
    }


    return <>
        {
            (typeof(chat.chatName) != 'undefined' && <div className='chatHeader'>
                <div className='chatName'>{chat.chatName}</div>
                <div className='chatManage'>
                    <div className='chatFounder'>founder: {founder}</div>
                    <div className='chatManagers'>
                        managers:
                        {
                        managers.map((manager) => {
                                return <div className='manager'>{manager}</div>
                            })
                        }
                    </div>
                </div>
                <div className='chatMembers'>
                    שששש
                </div>
            </div>)
        }
        {
            (typeof(chat.chatName) == 'undefined' && <div className='chatHeader'>{
                user2
            }</div>)
        }

    </>


}
