import { useState } from 'react'
import { useSelector } from 'react-redux'

import{findUser} from '../store/userActions'
import {AddToGroup} from './addToGroup'
import {creatNewChat} from '../store/chatActions'
import {updateUser} from '../store/userActions'
export function NewGroup(){
    const user = useSelector(state => state.userModule )

    const [groupName,setGroupName] = useState('')
    const [description,setDescription] = useState('')
    const [email,setEmail] = useState('')
    const [participantsList,setParticipants] = useState([])    

    const [userFound,setUserFound] = useState('')

    console.log('userFound',userFound)

    async function searchUsser() {
        try{
            const user = await findUser(email)
            setUserFound({id:user._id,name:user.name})
        }catch(err){
            console.log(err)
        }
    }

    async function addUuser() {
        try{
            const updateParticipants = [...participantsList,userFound.id]
            console.log('updateParticipants',updateParticipants)
            setParticipants(updateParticipants)
        }catch(err){
            console.log(err)
        }
    }

    async function newGrop(e){
        try{
            e.preventDefault()
            const Ngroup ={
                type: 'group',
                creator: user.userId,
                name: groupName,
                participants: [...participantsList,user.userId],
                managers: [user.userId],
                description: description
            }
            const newChat =  await creatNewChat(Ngroup)
    
            const userChats = (user.userChats)? user.userChats: []

            const updatUserChat = [...userChats, newChat.data._id]

            const updateChats = {
                chats:updatUserChat
            }
            updateUser(user.userId,updateChats)

            //update members
            participantsList.forEach(async function(participant){
                try{
                    const user = await findUser(participant)
                    console.log('aaaa',user.chats)
                    const updatUserChat = [...userChats, newChat.data._id]

                    const updateChats = {
                        chats:updatUserChat
                    }
        
                    updateUser(participant,updateChats)
    
                }catch(err){
                    // console.log(err)
                }

            })
            
            
        }catch(err){

        }
    }



    return <div className='newGroup'>
        <form onSubmit={(e) => newGrop(e)}>
            <input type='text' placeholder='Group name' value={groupName} onChange={(e) => setGroupName(e.target.value)}></input>
            <textarea placeholder='Group description' value={description} onChange={(e) => setDescription(e.target.value)} />
            <div>look for user</div>
            <input type='text' placeholder='Email' onChange={(e) => setEmail(e.target.value)}></input>
            <input type='button' value={'search'} onClick={() => searchUsser()}></input>
            <div className='userFound' onClick={() => {addUuser(userFound.id)}}>{userFound.name}</div>

            <AddToGroup participants={participantsList} />
            <input type='submit' value={'Creat group'}></input>
    
        </form>
    </div>
    
}
