import { useSelector } from 'react-redux'
import {ChatMenuHeader} from './chatMenuHeader'
import { useEffect, useState } from 'react'
import {NewGroup} from './newGroup'
import {NewChat} from './newChat'
import {ChatSide} from './chatSide'
import {ChatMenuActions} from './chatMenuActions'

export function ChatsMenu(){
    const user = useSelector(state => state.userModule )
    const [mod,setMod] = useState('chats')

    return <div className="chatsMenu">
        <div className="chatMenuHeader">
            <div className= "userMenu">
                userMenu
            </div>
            {/* <div className='chatMenuActions'>
                <div className="chatMenuBtn" onClick={() => setMod('newGroup')}>New Group</div>
                <div className="chatMenuBtn" onClick={() =>setMod('newChat')}>New Chat</div>
                <div className="chatMenuBtn" onClick={() =>setMod('chats')}>Chats</div>
            </div> */}
            <ChatMenuActions setMod={setMod} />
        </div>

       {
        ((mod == 'chats' && !user.userChats) && 'no chats')
       }
       {
        ((mod == 'chats' && user.userChats.length > 0) && <div>
            {user.userChats.map((chat) =>{
                return <ChatSide key={chat.chatId} chat={chat} />
            })}
        </div>)
       }
       {
        (mod == 'newGroup' && <NewGroup setMod={setMod} />)
       }
       {
        (mod == 'newChat' && <NewChat setMod={setMod} />)
       }
        
    </div>
}
