import { useState } from 'react'
import{findUser} from '../store/userActions'
import {NewGroup} from './newGroup'

export function ChatsMenu(){
    const user = useSelector(state => state.userModule )

    const [mod,setMod] = useState('chats')
    const [participants,setParticipants] = useState([])    

    const [email,setEmail] = useState('')
    const [userFound,setUserFound] = useState('')

    async function searchUsser() {
        try{
            const user = await findUser(email)
            setUserFound({id:user._id,name:user.name})
        }catch(err){
            console.log(err)
        }
    }

    async function addUuser() {
        try{
            const participantsList = participants
            const updateParticipants = [...participantsList,userFound.name]
            setParticipants(updateParticipants)
        }catch(err){
            console.log(err)
