import {updateUser,updateSomeUser} from '../store/userActions'

export function Participant({member}){
    const user = useSelector(state => state.userModule )
    const activeChat = useSelector(state => state.chatModule )
    const [participantsList,setParticipants] = useState([])  


    async function removeFromGroup(userId,memberChats){
        try{
            const updateParticipantsList = activeChat.participants.filter((participant) => participant !=userId)
            const userChatUpdat = memberChats.filter((chat) => chat.chatId != activeChat.chatId) 

            const updatParticipants = {
                participants: updateParticipantsList 
            }
            const updatChats = {
                chats:userChatUpdat
            }
            const updateParticipantsChat = await updateChat(activeChat.chatId,updatParticipants)
            const updateUserChats =  await updateSomeUser(userId,updatChats)
        }catch(err){}

    }

    async function addAsManager(userId,chatManagers){
        try{
            const updateChatManagers = [...chatManagers,userId]
            const updatParticipants = {
                managers: updateChatManagers 
            }
            const updateParticipantsChat = await updateChat(activeChat.chatId,updatParticipants)
        }catch(err){}

    }


    return <div className='HedaerUser'>
        <div>{member.name}</div>
            {(member.isManager &&
                <div>Manager</div>
            )}
            {((activeChat.managers.includes(user.userId) && !member.isManager) &&
                <div>
                    <button onClick={() => removeFromGroup(member._id,member.chats)}>Remove from group</button>
                    <button onClick={() => addAsManager(member._id,activeChat.managers)}>Add as manager</button>
                </div>
            )}
            {((activeChat.managers.includes(user.userId) && member.isManager) &&
                <div>
                    <button>Remove as manager</button>
                </div>
            )}
    </div>
}
