import axios, { AxiosError } from 'axios';
import { store } from './store.js'
import { SET_CHATID,SET_CHATNAME,SET_PARTICIPANTS,SET_MANAGERS,SET_MESSAGES,SET_DESCRIPTION } from './chatReducer.js'

export async function creatNewChat(chat){
    try{
        const newChat = await axios.post(`http://localhost:3030/api/chat`,chat)
        return newChat.data
    }catch(err){
    }
}

export async function setChat(chatId){
    try{
        console.log('gf',chatId)
        const chat = await axios.get(`http://localhost:3030/api/chat/${chatId}`)
        console.log(chat)

        store.dispatch({
            type: SET_CHATID,
            chatId: chat.data._id
        })
        store.dispatch({
            type: SET_CHATNAME,
            chatName: chat.data.name
        })
        store.dispatch({
            type: SET_PARTICIPANTS,
            participants: chat.data.participants
        })
        store.dispatch({
            type: SET_MANAGERS,
            managres: chat.data.managers
        })
        store.dispatch({
            type: SET_MESSAGES,
            massages: chat.data.messages
        })
        store.dispatch({
            type: SET_DESCRIPTION,
            description: chat.data.description
        })

    }catch(err){

    }
}
