export const SET_USERID = 'SET_USERID' 
export const SET_USERNAME = 'SET_USERNAE' 
export const SET_USEREMAIL = 'SET_USEREMAIL' 
export const SET_USERCHATS = 'SET_USERCHATS' 


const initialState = {
    userId: '',
    userName: '',
    userEmail:'',
    userChats:''
}

export function userReducer(state = initialState, action) {
    var newState = state
    switch (action.type) {
        case SET_USERID:
            newState = { ...state, userId: action.userId }
            break
        case SET_USERNAME:
            newState = { ...state, userId: action.userName }
            break
        case SET_USEREMAIL:
            newState = { ...state, userId: action.userEmail }
            break
        case SET_USERCHATS:
            newState = { ...state, userId: action.userChats }
            break
                        
        default:
    }
    // For debug:
    window.userState = newState
    console.log('State:', newState)
    return newState

}
