import { useEffect, useState, useRef } from "react"
import { useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import axios, { AxiosError } from 'axios';

import {ChatHeader} from '../components/chatHeader'


export function Video(){
    const navigate = useNavigate()


    const [userStream, setUserStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);
    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);

    newPeer.on('call', (call) => {
        navigator.mediaDevices.getUserMedia({ video: true, audio: true })
          .then((stream) => {
            setUserStream(stream); // שמירת ה-Stream של המשתמש
            call.answer(stream); // עונים לשיחה עם ה-Stream של המשתמש
  
            // קבלת הווידאו של המשתמש השני
            call.on('stream', (remoteStream) => {
              setRemoteStream(remoteStream);
            });
          });
      });
  
      setPeer(newPeer); // שמירת ה-peer ב-state

    return <div>
          <video ref={localVideoRef} muted autoPlay playsInline width="300"/>
      

      {remoteStream && (
        <div>
          <h2>Other User's Video</h2>
          <video
            ref={remoteVideoRef}
            autoPlay
            playsInline
            width="300"
          />
        </div>
      )}

      <div>
        <input
          type="text"
          placeholder="Enter other user's peer ID"
          onBlur={(e) => callUser(e.target.value)} // שולחים את ה-peer id של המשתמש השני
        />
      </div>
  
    <button onClick={() => window.close()}>close</button>

    </div>
}
