import { useState } from 'react'
import { useSelector } from 'react-redux'

import{findUser} from '../store/userActions'
import {creatNewChat} from '../store/chatActions'
import {updateUser} from '../store/userActions'
import {AddUser} from './addUser'


export function NewChat({setMod}){

    const user = useSelector(state => state.userModule)

    const [email,setEmail] = useState('')
    const [userFound,setUserFound] = useState()



    async function searchUsser() {
        try{
            const user = await findUser(email)
            setUserFound(user)
        }catch(err){
            console.log(err)
        }
    }


    async function newChat(e,userFound){
        try{
            e.preventDefault()
            const Nchat ={
                type: 'privet',
                creator: user.userId,
                participants: [...participantsList,user.userId],
            }
            const newChat =  await creatNewChat(Nchat)
    
            const userChats = (user.userChats)? user.userChats: []

            const updatUserChat = [...userChats, newChat.data._id]

            const updateChats = {
                chats:updatUserChat
            }
            updateUser(user.userId,updateChats)

            //update members
            participantsList.forEach(async function(participant){
                try{
                    const user = await findUser(participant)
                    const updatUserChat = [...userChats, newChat.data._id]

                    const updateChats = {
                        chats:updatUserChat
                    }
        
                    updateUser(participant,updateChats)
    
                }catch(err){
                    // console.log(err)
                }

            })
            setMod('chats')
            
        }catch(err){

        }
    }


    return <div className="newChat">
        <form onSubmit={(e) => newChat(e)} className='newGroupForm'>
            <div>Find user</div>
            <input type='text' placeholder='Email' onChange={(e) => setEmail(e.target.value)}></input>
            <input type='button' value={'search'} onClick={() => searchUsser()}></input>        
            {(userFound &&
                <div className='userFound'>
                    <div>Is this who your are looking for?</div>
                    <div className='userFdetails'>
                        <div className='userDetails'>
                            <div>{userFound.name}</div>
                            <div>{userFound.email}</div>
                        </div>
                        <div className='addUserAction' onClick={() => {newChat(userFound.id)}}>
                            Strat chat
                        </div>
                    </div>
                </div>
        )}
            <input type='submit' value={'Start chat'}></input>
        </form>
    </div>
}
