        const newUser = await axios.post(`http://localhost:3030/api/auth/signup`,user)
        console.log(newUser)
        if(newUser.status == '200')
        store.dispatch({
            type: SET_USERID,
            userId: newUser.data.newUser._id
        })
        store.dispatch({
            type: SET_USERNAME,
            userName: newUser.data.newUser.name
        })
        store.dispatch({
            type: SET_USEREMAIL,
            userEmail: newUser.data.newUser.email
        })
        store.dispatch({
            type: SET_USERCHATS,
