import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import {setChat} from '../store/chatActions'

import {findChat} from '../store/chatActions'
import {findMessage} from '../store/massageActions'

export function ChatSide({chat}){
    const user = useSelector(state => state.userModule)

    const [displayChat,setDisplayChat] = useState()
    const [lastMagage,setLlastMesage] = useState()

    useEffect(() =>{
        getChat(chat.chatId)
    },[])


    async function getChat(chatId){
        try{
            const chat = await findChat(chatId)
            if(chat.data.name){
                setDisplayChat(chat.data)
            }else{
                let chatName = chat.data.participants.filter((participant) => participant != user.userId)
                console.log('chatName',chatName)
                setDisplayChat(chat.data)
            }

        }catch(err){}
    }

    // console.log(displayChat)
    return <div key={chat.chatName} className='chatMenuName' onClick={() => setChat(chat.chatId)}>
        {displayChat.name}
    </div>
    
}
