import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import {findUser} from '../store/userActions'

export function ChatHeader({chat,mod,setMod,setUserId}){

    const user = useSelector(state => state.userModule)
    const [founder, setFounder] = useState('')
    const [managers,setManagers] = useState([])
    const [members,setMembers] = useState([])


    useEffect(() => {
        if(chat.type == 'group') setGroup(chat.founder)
    },[chat])


    async function setGroup(founderId){
        try{
            const founder = await findUser(founderId)
            const founderName = founder.name
            setFounder(founderName)
            const managersNames = []
            const membersNames = []
            for(let manager in chat.managers){
                const user = await findUser(chat.managers[manager])
                managersNames.push(user.name)
            }
            for(let member in chat.participants){
                const user = await findUser(chat.participants[member])
                membersNames.push(user.name)
            }
            setManagers(managersNames)
            setMembers(membersNames)
        }catch(err){}
    }

    function changeMod(){
        if(mod == 'chat'){
            if(chat.type == 'group'){
                setMod('chatManage')
            }else{
                setMod('user') 
                const userId = chat.participants.filter((participant) => participant.id != user.userId)
                console.log(userId[0])
                setUserId(userId[0].id)        
            }
        }else{
            setMod('chat')
        }
    }

    return (
        <>
        {chat.chatName ?(<div className='chatHeader'>
                <div className='chatName' onClick={() => changeMod()}>{chat.chatName}</div>
                <div className='chatManageHeader'>
                    <div className='HedaerUser'>founder: {founder}</div>
                    <div className='chatManagers'>
                        managers:
                        {
                        managers.map((manager) => {
                                return <div key={manager} className='HedaerUser'>{manager}</div>
                            })
                        }
                    </div>
                </div>
                <div className='chatMembers'>
                        members:
                        {
                        members.map((member) => {
                                return <div key={member} className='HedaerUser'>{member}</div>
                            })
                        }
                </div>
            </div>) :
            (typeof(chat.chatName) == 'undefined' && <div className='chatHeader'>
                <div className='chatName' onClick={() => changeMod()}>{chat.participants[0].name}</div>
            </div>)
        }
        </>
    )
}
