import io from 'socket.io-client'
import { initialState } from './userReducer'

import{addMsg} from'./chatActions'




const user = useSelector(state => state.userModule)

const socket = io.connect("http://localhost:3030",)

socket.emit('listen', initialState.user.userId);

// export function listen(userId){
//     socket.emit('chat', user.userId);

// }

export function joinChat(chatId){
    socket.emit('chat', chatId);
}

export function socketNewMsg(chatId){
    console.log('ID',chatId)
    socket.emit('newMsg', chatId);
}


socket.on('newMsg', function(data){
    addMsg(data)
})
