import { useSelector } from 'react-redux'
import { useEffect, useState } from 'react'

import {findUser} from '../store/userActions'


export function ChatUserMod({setMod,userId}){
    
    const logdInUser = useSelector(state => state.userModule )

    const [user,setUser] = useState()
    const [logdInUserChats,setULogdInUserChats] = useState([])


    useEffect(() =>{
        getUser(userId)
        getLogdInUserChats()
    },[userId])

    async function getUser(userId){
        try{
            const user = await findUser(userId)
            setUser(user)
        }catch(err){}
    }

    function getLogdInUserChats(){
        let userChats = []
        for(let chat in logdInUser.userChats){
            console.log('chat',logdInUser.userChats[chat])
            userChats.push(chat.chatId)
        }
        setULogdInUserChats(userChats)
    }

    console.log('userLogdIn.userChats',logdInUserChats)
    if(user){
        return <div className="chatUserMod">
            <div onClick={() => setMod('chat')}>Rreturn to chat</div>
            <div>{user.name}</div>
            <div>{user.email}</div>
            <div>{user.createdAt}</div>

            {/* {
                user.chats.map((chat) => {
                    if(logdInUserChats.userChats.includes(chat.chatId)){
                        return <div>'a'</div>
                    }else{
                        return <div>'b'</div>
                    }
                })
            } */}
        </div>
    }
}
