import axios from 'axios';
import { store } from './store.js'
import { SET_USERID,SET_USERNAM,SET_USEREMAIL,SET_USERCHATS } from './userReducer.js'


export async function newUser(user) {
    try{
        const newUser = await axios.post(`http://localhost:3030/api/auth/signup`,{user})
        console.log(newUser)
        if(newUser.status == '200')
        store.dispatch({
            type: SET_USERID,
            userId: newUser.data.newUser._id
        })
        store.dispatch({
            type: SET_USERNAM,
            userId: newUser.data.newUser._id
        })
        store.dispatch({
            type: SET_USEREMAIL,
            userId: newUser.data.newUser._id
        })
        store.dispatch({
            type: SET_USERCHATS,
            userId: newUser.data.newUser._id
        })


    }catch(err){
        console.log(err)
    }
    
}
