import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

import {findUser} from '../store/userActions'
import {AddNewUser} from './AddNewUser'
import {Participant} from './participant'

import {updateChat,resetGroup} from '../store/chatActions'
import {updateUser,updateSomeUser} from '../store/userActions'

export function ChatManage({setMod,setUserId}){

    const user = useSelector(state => state.userModule )
    const activeChat = useSelector(state => state.chatModule )
    const [participantsList,setParticipants] = useState([])  


    useEffect(() =>{
        getMmbers()
    },[activeChat])

    async function getMmbers(){
        try{
            const membersNames = []
            for(let member in activeChat.participants){
                const user = await findUser(activeChat.participants[member])
                user.isManager = false
                if(activeChat.managers.includes(user._id)) user.isManager = true
                membersNames.push(user)
            }
            setParticipants(membersNames)
        }catch(err){}
    }

    async function leaveGroup() {
        try{
            const participantsListUpdat =  activeChat.participants.filter((participant) => participant != user.userId)
            let managersUpdat
            if(activeChat.managers.includes(user.userId) && participantsListUpdat.length != 0){
                managersUpdat = activeChat.managers.filter((manager) => manager !=user.userId)
                if(managersUpdat.length  == 0){
                    managersUpdat.push(activeChat.participants[0])
                }
            }
            //update user
            const userChatUpdat = user.userChats.filter((chat) => chat.chatId != activeChat.chatId) 
    
            const updatParticipants = {
                participants:participantsListUpdat
            }
            const updatManagers = {
                managers:managersUpdat
            }
            const updatChats = {
                chats:userChatUpdat
            }
    
            const updateParticipantsChat = await updateChat(activeChat.chatId,updatParticipants)
            const updatChatManagers = await updateChat(activeChat.chatId,updatManagers)
            const updateUserChats =  await updateUser(user.userId,updatChats)
            resetGroup()
            // setMod('chat')
        }catch(err){}
    }

    return <div className='chatManage'>
        {(activeChat.type == 'group' &&
            <>
            <div className='groupDescription'>
                {activeChat.description}
            </div>
            {(activeChat.managers.includes(user.userId) &&
                <AddNewUser participantsList={participantsList} setParticipants={setParticipants} />
            )}
            <div className='participantsList'>
                {            
                participantsList.map((member) => {
                    return <Participant key={member._id} member={member} setMod={setMod} setUserId={setUserId} />
                })     
                }
            </div>
            
            </>
        )}

        <div className='groupActions'>
            <button onClick={() => leaveGroup()}>leave group</button>
            <div>By clicking you will leave the group and it will be deleted from your chat list</div>
        </div>
    </div>
}
