import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'

import {AddUser} from './addUser'

export function ChatManage(){

    const activeChat = useSelector(state => state.chatModule )
    const [participantsList,setParticipants] = useState([])  
    
    console.log('participantsList',activeChat.participants)


    async function getSender(id){
        try{
            const sender = findUser(id)
            setSender(sender.data)
        }catch(err){}
    }


    return <div>
        <AddUser participantsList={participantsList} setParticipants={setParticipants} />
        {
            activeChat.participants.map((participant) =>{
                return <div key={participant}>{participant}</div>
            })
        
        }
    </div>
}
