import { useSelector } from 'react-redux'
import {ChatMenuHeader} from './chatMenuHeader'
import { useEffect, useState } from 'react'
import {NewGroup} from './newGroup'
import {setChat} from '../store/chatActions'
import {NewChat} from './newChat'
import {findChat} from '../store/chatActions'

export function ChatsMenu(){
    const user = useSelector(state => state.userModule )
    const [mod,setMod] = useState('chats')
    const [chats,setChats] = useState([])
    console.log(user.userChats)
    let chatsNames = []
    useEffect(() => {
        setChatName(user.userChats)
        // user.userChats.forEach((chats) => setChatName(chats))
    },[])

    async function chatName(chat){
        try{
            const name = await findChat(chat)
            return name.data.name
        }catch(err){}
    }
    async function setChatName(userChats) {
        try{
            let a = []
            for(let chat of userChats){
                try{
                    const name = await findChat(chat)
                    console.log('chatName',name.data.name)
                    a.push(name.data.name)    
                }catch(err){}
            }
            console.log('a',a)
        }catch(err){

        }
    }

    console.log('chats', chats)
