import { useSelector } from 'react-redux'
import {NewMsg} from './newMsg'
import {ChatHeader} from './chatHeader'
import {Message} from './message'
import { useEffect, useState } from 'react'
import {ChatManage} from './chatManage'
import {ChatUserMod} from './chatUserMod'
import {joinChat} from '../store/socket'


export function Chat(){
    const activeChat = useSelector(state => state.chatModule )

    const [mod,setMod] = useState('chat')
    const [userId,setUserId] = useState()

    useEffect(() =>{
        setMod('chat')
    },[])

    useEffect(() =>{
        setMod('chat')
        joinChat(activeChat.chatId)
    },[activeChat])


    if(activeChat.chatId && activeChat.messages && mod == 'chat' ){
        return <div className="chat">
            <ChatHeader chat={activeChat} mod={mod} setMod={setMod} />
                <div className='chatBody'>
                    {((activeChat.messages && mod == 'chat') &&
                        <>
                            {activeChat.messages.map((message) =>{
                                return <Message key={message}  messageId={message} />
                            })}
                        </>
                    )}
            </div>
            <NewMsg />
        </div>
    }else if(activeChat.chatId && activeChat.messages && mod == 'chatManage' && activeChat.type == 'group'){
        return <div className="chat">
            <ChatHeader chat={activeChat} mod={mod} setMod={setMod} />
            <ChatManage setMod={setMod} setUserId={setUserId} />
        </div> 
    }else if(activeChat.chatId && activeChat.messages && mod == 'user' ){
        return <div className="chat">
            <ChatHeader chat={activeChat} mod={mod} setMod={setMod} />
            <ChatUserMod mod={mod} setMod={setMod} userId={userId} />
        </div>
    }
    
}
