import { useSelector } from 'react-redux'
import { useEffect, useState } from 'react'

import {findUser} from '../store/userActions'


export function ChatUserMod({setMod,userId}){
    
    const userLogdIn = useSelector(state => state.userModule )

    const [user,setUser] = useState()

    useEffect(() =>{
        getUser(userId)
    },[userId])

    async function getUser(userId){
        try{
            const user = await findUser(userId)
            setUser(user)
        }catch(err){}
    }

    console.log(user.chats)
    if(user){
        return <div className="chatUserMod">
            <div onClick={() => setMod('chat')}>Rreturn to chat</div>
            <div>{user.name}</div>
            <div>{user.email}</div>
            <div>{user.createdAt}</div>

            {
                user.chats.map((chat) => {
                    if(userLogdIn.userChats.includes(chat.chatId)){
                        console.log('a')
                    }else{
                        console.log('b')
                    }
                })
            }
        </div>
    }
}
