import io from 'socket.io-client'

import{addMsg} from'./chatActions'

const socket = io.connect("http://localhost:3030",'aviv')

// socket.emit('listen', {aaa:'bbbb'});

export function listen(userId){
    socket.emit('chat', userId);

}

export function joinChat(chatId){
    socket.emit('chat', chatId);
}

export function socketNewMsg(chatId){
    console.log('ID',chatId)
    socket.emit('newMsg', chatId);
}


socket.on('newMsg', function(data){
    addMsg(data)
})
