import { useState, useEffect } from 'react';
import{findUser} from '../store/userActions'


export function AddUser({participantsList , setParticipants}){


    
    const [email,setEmail] = useState('')
    const [userFound,setUserFound] = useState({})
    const [participantObj,setParticipantObj] = useState([])


    async function searchUsser() {
        try{
            const user = await findUser(email)
            setUserFound(user)
        }catch(err){
            console.log(err)
        }
    }
    console.log('bbbb',userFound)

    async function addUuser() {
        try{
            console.log('cccc',userFound)
            console.log('aaaa',participantsList.includes(userFound.id))
            console.log('participantsList',participantsList)
            if(!participantsList.includes(userFound.id)){
                const updateParticipants = [...participantsList,userFound.id]
                setParticipantObj([...participantObj,userFound])
                setParticipants(updateParticipants)
    
            }
        }catch(err){
            console.log(err)
        }
    }

    return <div className='addUser'>
        
        <div>look for user</div>
        <input type='text' placeholder='Email' onChange={(e) => setEmail(e.target.value)}></input>
        <input type='button' value={'search'} onClick={() => searchUsser()}></input>        
        <div className='userFound'>
            <div className='userDetails'>
                <div>{userFound.name}</div>
                <div>{userFound.email}</div>
            </div>

            <div className='addUserAction' onClick={() => {addUuser(userFound.id)}}>
                Add user to group
            </div>

        </div>

        <div className='participantsList'>
        {
            participantObj.map((participant) =>{
                return <div className='participant' key={participant._id}>
                    {participant.name}
                    {participant.email}
                </div>
            })
        }
    
        </div> 
        
    </div>
}
