import { useEffect, useState } from 'react'
import {setChat} from '../store/chatActions'

import {findChat} from '../store/chatActions'

export function ChatSide({chat}){

    const [displayChat,setDisplayChat] = useState()

    useEffect(() =>{
        getChat(chat.chatId)
    },[])

    async function getChat(chatId){
        try{
            const chat = await findChat(chatId)
            setDisplayChat(chat.data)
        }catch(err){}
    }


    console.log('chat',displayChat)
    console.log('chat',chat)

    return <div key={chat.chatName} className='chatMenuName' onClick={() => setChat(chat.chatId)}>
        {chat.chatName}
    </div>
}
