import { useSelector } from 'react-redux'
import {NewMsg} from './newMsg'
import {ChatHeader} from './chatHeader'
import {Message} from './message'
import { useEffect, useState } from 'react'
import {ChatManage} from './chatManage'
import {ChatUserMod} from './chatUserMod'
import {joinChat} from '../store/socket'


export function Chat({mod,setMod}){
    const activeChatId = useSelector(state => state.chatModule.chatId)
    const activeChatMessages = useSelector(state => state.chatModule.messages)
    const [userId,setUserId] = useState()

    useEffect(() =>{
        if(activeChatId) joinChat(activeChatId)
    },[activeChatId])

    if (!activeChatId || !activeChatMessages) {
        return null
    }


    const renderChat = () => (
        <>
            <div className='chatBody'>
                {activeChatMessages.map((message) =>{
                    return <Message key={message}  messageId={message} />
                })}
            </div>
            <NewMsg />
        </>
    )

    const renderChatManage = () =>(<ChatManage setMod={setMod} setUserId={setUserId} />)
    const renderUser =() =>(<ChatUserMod mod={mod} setMod={setMod} userId={userId} />)

    const chat ={
        chat: renderChat,
        chatManage: renderChatManage,
        user: renderUser
    }

    const renderChatContent = chat[mod] || (() => <div>שגיאה</div>);

    return <div className='chat'>
        <ChatHeader chat={activeChatId} mod={mod} setMod={setMod} />
        {renderChatContent()}
    </div>
    
}
