import { useState, useEffect } from 'react';
import{findUser} from '../store/userActions'


export function AddUser({participantsList , setParticipants}){


    
    const [email,setEmail] = useState('')
    const [userFound,setUserFound] = useState('')


    async function searchUsser() {
        try{
            const user = await findUser(email)
            setUserFound({id:user._id,name:user.name})
        }catch(err){
            console.log(err)
        }
    }


    async function addUuser() {
        try{
            const updateParticipants = [...participantsList,userFound.id]
            setParticipants(updateParticipants)
        }catch(err){
            console.log(err)
        }
    }


    let [participantsObj,setParticipantsObj] = useState([])

    useEffect(() =>{
        addParticipantsObj(participantsList)
    },[participantsList])

    
    async function addParticipantsObj(participantsList){
        participantsList.forEach(async function(participant){
            console.log('element')
            const user = await findUser(participant)
            setParticipantsObj([...participantsObj,user])
        });

        // try{
        //     participants.forEach(async function(participant){
        //         const user = await findUser(participant)
        //         console.log('user',user)
        //         participantsObj = [...participantsObj,user]
        //     })
        // }catch(err){}
    }



    return <div className='addUser'>
        
        <div>look for user</div>
        <input type='text' placeholder='Email' onChange={(e) => setEmail(e.target.value)}></input>
        <input type='button' value={'search'} onClick={() => searchUsser()}></input>

        
        
        <div className='userFound' onClick={() => {addUuser(userFound.id)}}>
            {userFound.name}
        </div>
        <div className='participantsList'>
        {
            participantsObj.map((participant) =>{
                return <div className='participant' key={participant._id}>
                    {participant.name}
                    {participant.email}
                </div>
            })
        }
    
        </div> 
        
    </div>
}
