import { useEffect, useState, useRef } from "react";
import { useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Peer from 'peerjs';

export function Video() {
    const user = useSelector(state => state.userModule);

    const [peer, setPeer] = useState(null);
    const [userStream, setUserStream] = useState(null);
    const [remoteStream, setRemoteStream] = useState(null);

    const localVideoRef = useRef(null);
    const remoteVideoRef = useRef(null);

    useEffect(() => {
        if (!user.userId) return;

        const newPeer = new Peer(user.userId); // יצירת Peer עם ה-ID של המשתמש

        newPeer.on('open', (id) => {
            console.log('My Peer ID:', id);
        });

        newPeer.on('call', (call) => {
            navigator.mediaDevices.getUserMedia({ video: true, audio: true })
                .then((stream) => {
                    setUserStream(stream);
                    if (localVideoRef.current) {
                        localVideoRef.current.srcObject = stream;
                    }
                    call.answer(stream); // עונים לשיחה עם הזרם המקומי

                    call.on('stream', (remoteStream) => {
                        setRemoteStream(remoteStream);
                        if (remoteVideoRef.current) {
                            remoteVideoRef.current.srcObject = remoteStream;
                        }
                    });
                })
                .catch(err => console.error('Error getting media stream:', err));
        });

        setPeer(newPeer);

        return () => {
            newPeer.destroy();
        };
    }, [user.userId]);

    const callUser = (otherPeerId) => {
        if (!peer || !userStream) return;

        const call = peer.call(otherPeerId, userStream);
        call.on('stream', (remoteStream) => {
            setRemoteStream(remoteStream);
            if (remoteVideoRef.current) {
                remoteVideoRef.current.srcObject = remoteStream;
            }
        });
    };

    return (
        <div>
            <div>
                <h2>My Video</h2>
                <video ref={localVideoRef} muted autoPlay playsInline width="300" />
            </div>

            {remoteStream && (
                <div>
                    <h2>Other User's Video</h2>
                    <video ref={remoteVideoRef} autoPlay playsInline width="300" />
                </div>
            )}

            <div>
                <input
                    type="text"
                    placeholder="Enter other user's peer ID"
                    onBlur={(e) => callUser(e.target.value)}
                />
            </div>

            <button onClick={() => window.close()}>Close</button>
        </div>
    );
}
