import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import {findUser} from '../store/userActions'

export function ChatHeader({chat,mod,setMod,setUserId}){

    const user = useSelector(state => state.userModule)
    const [founder, setFounder] = useState('')
    const [managers,setManagers] = useState([])
    const [members,setMembers] = useState([])


    // useEffect(() => {
    //     if(chat.type == 'group') setGroup(chat.founder)
    // },[chat])

    // console.log(chat)


    // async function setGroup(founderId){
    //     try {
    //         const usersIds = [founderId, ...chat.managers, ...chat.participants];
            
    //         const users = await Promise.all(usersIds.map(id => findUser(id)));
            
    //         const usersById = users.reduce((acc, user) => {
    //             acc[user._id] = user.name;
    //             return acc;
    //         }, {});
            
    //         setFounder(usersById[founderId]);
    //         setManagers(chat.managers.map(managerId => usersById[managerId]));
    //         setMembers(chat.participants.map(memberId => usersById[memberId]));
    //     }catch(err){}
    // }

    function changeMod(){
        if(mod === 'chat'){
            if(chat.type === 'group'){
                setMod('chatManage')
            }else{
                setMod('user') 
                const userId = chat.participants.find(participant => participant.id !== user.userId)?.id;
                if (userId) {
                    setUserId(userId);
                }            }
        }else{
            setMod('chat')
        }
    }

    function userDetails(userId){
        // console.log(usersById[userId])
        setMod('user') 
        setUserId(userId)
    }


    return (
        <>
            {chat.chatName ?(<div className='chatHeader'>
                <div className='chatName' onClick={() => changeMod()}>{chat.chatName}</div>
                <div className='chatManageHeader'>
                    {<div className='HedaerUser' onClick={() => userDetails(chat.founder.id)}>founder: {chat.founder.name}</div>}
                    <div className='chatManagers'>
                        managers:
                        {
                        chat.managers.map((manager) => {
                                return <div key={manager} className='HedaerUser'>{manager.name}</div>
                            })
                        }
                    </div>
                </div>
                <div className='chatMembers'>
                        members:
                        {
                        chat.participants.map((participant) => {
                                return <div key={participant.id} className='HedaerUser'>{participant.name}</div>
                            })
                        }
                </div>
                </div>) :
                (typeof(chat.chatName) == 'undefined' && <div className='chatHeader'>
                <div className='chatName' onClick={() => changeMod()}>{chat.participants[0].name}</div>
                </div>)
            }
        </>
    )
}
