import { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import {findUser} from '../store/userActions'

export function ChatHeader({chat,mod,setMod}){

    const user = useSelector(state => state.userModule)
    const [user2,setUser2] = useState('')
    const [founder, setFounder] = useState('')
    const [managers,setManagers] = useState([])
    const [members,setMembers] = useState([])


    console.log('chatchat',chat)

    useEffect(() => {
        if(chat.type == 'privet'){
            const user2Id = chat.participants.filter((participant) => participant != user.userId)
            findUser2(user2Id)
        }else{
            setGroup(chat.founder)
        }
    },[chat])

    async function findUser2(id) {
        try{
            const user = await findUser(id)
            setUser2(user.name)
        }catch(err){}
    }

    async function setGroup(founderId){
        try{
            const founder = await findUser(founderId)
            const founderName = founder.name
            setFounder(founderName)
            const managersNames = []
            const membersNames = []
            for(let manager in chat.managers){
                // console.log(chat.managers)
                const user = await findUser(chat.managers[manager])
                managersNames.push(user.name)
            }
            for(let member in chat.participants){
                // console.log(chat.managers)
                const user = await findUser(chat.participants[member])
                membersNames.push(user.name)
            }
            setManagers(managersNames)
            setMembers(membersNames)
        }catch(err){}
    }

    function changeMod(){
        if(mod == 'chat'){
            setMod('chatManage')
        }else{
            setMod('chat')
        }
    }

    console.log('user2',user2)
    return <>
        {
            (typeof(chat.chatName) != 'undefined' && <div className='chatHeader'>
                <div className='chatName' onClick={() => changeMod()}>{chat.chatName}</div>
                <div className='chatManage'>
                    <div className='HedaerUser'>founder: {founder}</div>
                    <div className='chatManagers'>
                        managers:
                        {
                        managers.map((manager) => {
                                return <div className='HedaerUser'>{manager}</div>
                            })
                        }
                    </div>
                </div>
                <div className='chatMembers'>
                        members:
                        {
                        members.map((member) => {
                                return <div className='HedaerUser'>{member}</div>
                            })
                        }
                </div>
            </div>)
        }
        {
            (typeof(chat.chatName) == 'undefined' && <div className='chatHeader'>
                <div className='chatName' onClick={() => changeMod()}>{user2}</div>
            </div>)
        }
    </>
}
